:root{
  --glass-blur:20px;
  --glass-bg:rgba(255,255,255,0.4);
  --glass-border:rgba(255,255,255,0.4);
  --glass-bg-dark:rgba(0,0,0,0.3);
  --glass-border-dark:rgba(255,255,255,0.1);
}
body{
  background:linear-gradient(135deg,#a0c1ff 0%,#f6f9ff 100%);
  min-height:100vh;
  transition:background-color .3s;
}
body.glass-dark{
  background:linear-gradient(135deg,#0f2027 0%,#203a43 100%);
}
#app,.v-main,.container,.v-card,.v-sheet,.v-dialog .v-card,.v-navigation-drawer,.v-app-bar{
  background:var(--glass-bg) !important;
  backdrop-filter:blur(var(--glass-blur)) saturate(180%);
  -webkit-backdrop-filter:blur(var(--glass-blur)) saturate(180%);
  border:1px solid var(--glass-border);
  border-radius:16px !important;
  box-shadow:0 8px 32px rgba(31,38,135,.25);
  transition:background .3s,border-color .3s;
}
body.glass-dark #app,body.glass-dark .v-main,body.glass-dark .container,body.glass-dark .v-card,body.glass-dark .v-sheet,body.glass-dark .v-dialog .v-card,body.glass-dark .v-navigation-drawer,body.glass-dark .v-app-bar{
  background:var(--glass-bg-dark) !important;
  border-color:var(--glass-border-dark) !important;
  box-shadow:0 8px 32px rgba(0,0,0,.45) !important;
}
.v-btn{
  background:rgba(255,255,255,.25) !important;
  border:1px solid var(--glass-border) !important;
  backdrop-filter:blur(10px) !important;
  -webkit-backdrop-filter:blur(10px) !important;
  transition:background .3s !important;
}
body.glass-dark .v-btn{
  background:rgba(255,255,255,.1) !important;
  border-color:var(--glass-border-dark) !important;
}
body.glass-disabled,body.glass-disabled *{
  backdrop-filter:none !important;
  -webkit-backdrop-filter:none !important;
}
