code.js:
const GlassPro = {
  init() {
    this.injectControl();
    this.bindHover();
  },
  injectControl() {
    const panel = document.createElement('div');
    panel.id = 'glass-pro-panel';
    panel.innerHTML = '<button id="glass-pro-dark">🌙</button><input id="glass-pro-blur" type="range" min="0" max="40" value="20" />';
    panel.style.position = 'fixed';
    panel.style.top = '10px';
    panel.style.right = '10px';
    panel.style.zIndex = '9999';
    document.body.appendChild(panel);
    document.getElementById('glass-pro-dark').addEventListener('click', () => {
      document.body.classList.toggle('glass-dark-mode');
    });
    document.getElementById('glass-pro-blur').addEventListener('input', e => {
      document.documentElement.style.setProperty('--glass-blur', e.target.value + 'px');
    });
  },
  bindHover() {
    let ticking = false;
    document.addEventListener('mousemove', e => {
      if (!ticking) {
        window.requestAnimationFrame(() => {
          document.documentElement.style.setProperty('--glass-pos-x', e.clientX + 'px');
          document.documentElement.style.setProperty('--glass-pos-y', e.clientY + 'px');
          ticking = false;
        });
        ticking = true;
      }
    });
  }
};
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', () => GlassPro.init());
} else {
  GlassPro.init();
}

