:root {
  --glass-blur: 20px;
  --glass-bg-light: rgba(255,255,255,0.15);
  --glass-bg-dark: rgba(0,0,0,0.25);
  --glass-border-light: rgba(255,255,255,0.4);
  --glass-border-dark: rgba(0,0,0,0.4);
  --glass-pos-x: 50%;
  --glass-pos-y: 50%;
}
body {
  background: var(--glass-bg-light);
  backdrop-filter: blur(var(--glass-blur));
  -webkit-backdrop-filter: blur(var(--glass-blur));
  transition: backdrop-filter 0.3s ease;
}
body::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
  background: radial-gradient(circle at var(--glass-pos-x) var(--glass-pos-y), rgba(255,255,255,0.3), transparent 70%);
  transition: background 0.2s ease;
}
.glass-card {
  background: var(--glass-bg-light);
  border: 1px solid var(--glass-border-light);
  border-radius: 12px;
  backdrop-filter: blur(calc(var(--glass-blur) + 5px));
  -webkit-backdrop-filter: blur(calc(var(--glass-blur) + 5px));
  box-shadow: 0 4px 30px rgba(0,0,0,0.1);
  will-change: transform;
}
body.glass-dark-mode {
  background: var(--glass-bg-dark);
  color: #fff;
}
body.glass-dark-mode::before {
  background: radial-gradient(circle at var(--glass-pos-x) var(--glass-pos-y), rgba(0,0,0,0.35), transparent 70%);
}
body.glass-dark-mode .glass-card {
  background: var(--glass-bg-dark);
  border: 1px solid var(--glass-border-dark);
  box-shadow: 0 4px 30px rgba(0,0,0,0.6);
}
#glass-pro-panel input[type=range] {
  width: 160px;
}
